CREATE TABLE profile (id BIGINT AUTO_INCREMENT, sf_guard_user_id INT, cedula VARCHAR(8), nombres VARCHAR(255), apellidos VARCHAR(255), email VARCHAR(255), departamento_id BIGINT, INDEX sf_guard_user_id_idx (sf_guard_user_id), INDEX departamento_id_idx (departamento_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE articulo (id INT AUTO_INCREMENT, descripcion VARCHAR(80) NOT NULL, categoria_id INT NOT NULL, marca_id INT NOT NULL, fecha_vencimiento DATE, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, deleted_at DATETIME, INDEX categoria_id_idx (categoria_id), INDEX marca_id_idx (marca_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE categoria (id INT AUTO_INCREMENT, descripcion VARCHAR(80) NOT NULL, activo TINYINT(1), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, deleted_at DATETIME, UNIQUE INDEX IX_categoria_1_idx (descripcion), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE departamento (id BIGINT AUTO_INCREMENT, descripcion VARCHAR(80) NOT NULL, activo TINYINT(1), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, deleted_at DATETIME, UNIQUE INDEX IX_departamento_1_idx (descripcion), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE detalle_solicitud (id INT AUTO_INCREMENT, solicitud_id INT, articulo_id INT, cantidad INT, INDEX articulo_id_idx (articulo_id), INDEX solicitud_id_idx (solicitud_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE inventario (id BIGINT AUTO_INCREMENT, articulo_id INT, cantidad BIGINT NOT NULL, fecha_vencimiento DATE NOT NULL, departamento_id BIGINT, INDEX articulo_id_idx (articulo_id), INDEX departamento_id_idx (departamento_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE marca (id INT AUTO_INCREMENT, descripcion VARCHAR(80) NOT NULL, activo TINYINT(1), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, deleted_at DATETIME, UNIQUE INDEX IX_marca_1_idx (descripcion), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id INT, permission_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id INT AUTO_INCREMENT, user_id INT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id, ip_address)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id INT AUTO_INCREMENT, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id INT, group_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id INT, permission_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
CREATE TABLE solicitud (id INT AUTO_INCREMENT, departamento_id BIGINT, sf_guard_user_id INT, aprobada TINYINT(1) DEFAULT '0', predespachada TINYINT(1) DEFAULT '0', despachada TINYINT(1) DEFAULT '0', recibida TINYINT(1) DEFAULT '0', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, deleted_at DATETIME, INDEX departamento_id_idx (departamento_id), INDEX sf_guard_user_id_idx (sf_guard_user_id), PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE profile ADD CONSTRAINT profile_sf_guard_user_id_sf_guard_user_id FOREIGN KEY (sf_guard_user_id) REFERENCES sf_guard_user(id);
ALTER TABLE profile ADD CONSTRAINT profile_departamento_id_departamento_id FOREIGN KEY (departamento_id) REFERENCES departamento(id);
